const ProductDetail = ({ product }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { id } = useParams();
  const user = useSelector((state) => state.auth.user);
  const {
    name,
    description,
    price,
    stock,
    images,
    discount,
    discountStartDate,
    discountEndDate,
    numberOfReviews,
    ratings,
  } = product;
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const cartLoading = useSelector((state) => state.cart.fulfilled);
  const cartItems = useSelector((state) => state.cart.items);
  const [quantity, setQuantity] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const { reviews } = useSelector((state) => state.reviews);
  console.log("Product: ", product);
  useEffect(() => {
    if (cartItems.length === 0 && !cartLoading) {
      dispatch(getCart());
    }
  }, [dispatch, cartItems.length, cartLoading]);

  useEffect(() => {
    dispatch(getReviewsByProductId(id));
  }, [dispatch, id]);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const handleAddToCart = async (e) => {
    e.preventDefault();
    if (user) {
      setIsLoading(true);
      await dispatch(addToCart({ productId: id, quantity: 1 }));
      await dispatch(getCart()); // Load lại giỏ hàng sau khi thêm sản phẩm
      setIsLoading(false);
      dispatch(openCartSidebar()); // Mở sidebar
    } else {
      Swal.fire({
        icon: "warning",
        title: "Please login",
        text: "You need to login to view your cart.",
        showCancelButton: true,
        confirmButtonText: "Login",
        cancelButtonText: "Cancel",
      }).then((result) => {
        if (result.isConfirmed) {
          navigate("/login");
        }
      });
    }
  };

  const handleImageClick = (image) => {
    setCurrentImageIndex(images.indexOf(image));
  };

  const handleQuantityChange = (type) => {
    if (type === "increment" && quantity < stock) {
      setQuantity((prev) => prev + 1);
    } else if (type === "decrement" && quantity > 1) {
      setQuantity((prev) => prev - 1);
    }
  };

  const averageRating =
    reviews.length > 0
      ? reviews.reduce((acc, review) => acc + review.rating, 0) / reviews.length
      : 0;

  const ratingCounts =
    reviews.length > 0
      ? [5, 4, 3, 2, 1].map(
          (rating) =>
            reviews.filter((review) => review.rating === rating).length
        )
      : [0, 0, 0, 0, 0];

  const renderStars = (rating) => {
    const fullStars = Math.floor(rating);
    const halfStar = rating % 1 >= 0.5;
    const stars = [];

    for (let i = 0; i < fullStars; i++) {
      stars.push(
        <Star
          key={`full-${i}`}
          src="data:image/png;base64,"
          alt="star--v1"
        />
      );
    }

    if (halfStar) {
      stars.push(
        <Star
          key="half"
          src="data:image/png;base64,"
          alt="star-half-empty"
        />
      );
    }

    const emptyStars = 5 - stars.length;
    for (let i = 0; i < emptyStars; i++) {
      stars.push(
        <Star
          key={`empty-${i}`}
          src="data:image/png;base64,"
          alt="star--v1"
        />
      );
    }

    return stars;
  };

  const now = new Date();
  const isDiscountValid =
    discount &&
    new Date(discountStartDate) <= now &&
    new Date(discountEndDate) >= now;

  const discountedPrice = isDiscountValid
    ? price - (price * discount) / 100
    : price;
  return (
    <ProductContainer>
      {isLoading && (
        <div className="loading">
          <div></div>
        </div>
      )}
      {/* Breadcrumb */}
      {/* Product Content */}
      <ProductContent>
        {/* Image Gallery */}
        <ImageGallery>
          {images.map((image, index) => (
            <Thumbnail
              key={index}
              src={image}
              alt={`Thumbnail ${index + 1}`}
              onClick={() => handleImageClick(image)}
            />
          ))}
          <MainImage src={images[currentImageIndex]} alt="Main Product" />
        </ImageGallery>

        {/* Product Info */}
        <ProductInfo>
          <ProductTitle>{name}</ProductTitle>
          <RatingContainer>
            <Stars>{renderStars(ratings)}</Stars>
            <ReviewsCount>({numberOfReviews} Reviews)</ReviewsCount>
          </RatingContainer>
          <Price>
            {isDiscountValid ? (
              <>
                <span style={{ textDecoration: "line-through", color: "#999" }}>
                  ${price.toFixed(2)}
                </span>{" "}
                <span style={{ color: "#e91e63" }}>
                  ${discountedPrice.toFixed(2)}
                </span>
              </>
            ) : (
              <span>${price.toFixed(2)}</span>
            )}
          </Price>
          <div style={{ display: "flex", gap: "10px" }}>
            <StockStatus>{stock > 0 ? "In Stock" : "Out of Stock"}</StockStatus>
            <p>({stock})</p>
          </div>
          <Description>{description}</Description>
          <Options>
            {/* Colors */}
            <div>
              Colours:
              <Option style={{ backgroundColor: "#FFFFFF" }} />
              <Option style={{ backgroundColor: "#FF4B4B" }} />
            </div>
            {/* Sizes */}
            <div>
              Size:
              <Option>XS</Option>
              <Option>S</Option>
              <Option>M</Option>
              <Option>L</Option>
              <Option>XL</Option>
            </div>
          </Options>

          {/* Quantity Selector */}
          <QuantitySelector>
            <button onClick={() => handleQuantityChange("decrement")}>-</button>
            <span>{quantity}</span>
            <button onClick={() => handleQuantityChange("increment")}>+</button>
          </QuantitySelector>

          {/* Add to Cart */}
          <AddToCartButton disabled={stock <= 0} onClick={handleAddToCart}>
            {stock > 0 ? "Add to Cart" : "Out of Stock"}
          </AddToCartButton>
          {/* Additional Info */}
          <AdditionalInfo>
            <InfoBlock>
              <strong>Free Delivery</strong>
              <p>Enter your postal code for Delivery Availability</p>
            </InfoBlock>
            <InfoBlock>
              <strong>Return Delivery</strong>
              <p>Free 30 Days Delivery Returns. Details</p>
            </InfoBlock>
          </AdditionalInfo>
        </ProductInfo>
      </ProductContent>

      {/* Review Section */}
      <ReviewContainer>
        <ReviewSummary>
          <ReviewSummaryTitle>Review Summary</ReviewSummaryTitle>
          <ReviewSummaryItem>
            <span>Average Rating</span>
            <span>{averageRating.toFixed(1)} ⭐</span>
          </ReviewSummaryItem>
          <BarChartContainer>
            {ratingCounts.map((count, index) => (
              <Bar key={index}>
                <BarLabel>{5 - index}⭐</BarLabel>
                <BarBackground>
                  <BarFill
                    color={
                      reviews.length > 0
                        ? [
                            "#4caf50",
                            "#8bc34a",
                            "#cddc39",
                            "#ffeb3b",
                            "#f44336",
                          ][index]
                        : "#fff"
                    }
                    width={
                      reviews.length > 0 ? (count / reviews.length) * 100 : 100
                    }
                  />
                </BarBackground>
                <span>{count}</span>
              </Bar>
            ))}
          </BarChartContainer>
        </ReviewSummary>

        <ReviewList>
          {reviews.map((review) => (
            <ReviewItem key={review._id}>
              <ReviewHeader>
                <ReviewAuthor>{review.user_id.name}</ReviewAuthor>
                <ReviewDate>
                  {new Date(review.createdAt).toLocaleDateString()}
                </ReviewDate>
              </ReviewHeader>
              <ReviewStars>
                {Array.from({ length: review.rating }, (_, index) => (
                  <span key={index}>⭐</span>
                ))}
              </ReviewStars>
              <ReviewContent>{review.comment}</ReviewContent>
            </ReviewItem>
          ))}
        </ReviewList>
      </ReviewContainer>

      <CartSidebar />
    </ProductContainer>
  );
};

export default ProductDetail;
